//import 'react-native-gesture-handler';
import React, { Component, useState, useEffect } from "react";
import { View, Text } from "react-native";
import { connect } from "react-redux";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
//import { Ionicons } from "@expo/vector-icons";
import { MaterialCommunityIcons, AntDesign } from "@expo/vector-icons";

import * as SecureStore from "expo-secure-store";
//import AuthScreen from './components/auth';
import Welcome from "./screens/Welcome";
import SignInScreen from "./screens/SignInScreen";
import SignUpScreen from "./screens/RegisterScreen";
import MainScreen from "./screens/MainScreen";
import HomeScreen from "./screens/HomeScreen";
//import MainScreen from "./screens/MainScreen";

//Admin routes
import UserListScreen from "./screens/UserListScreen";

import { useDispatch, useSelector } from 'react-redux'
import ProductScreen from "./screens/ProductScreen";
import ProfileScreen from "./screens/ProfileScreen";
import CartScreen from "./screens/CartScreen";
import { login } from "../actions/userActions";
//import ProfileScreen from './components/user/profile/profile';
//import { Stack, HomeStack, VideosStack, screenOptions } from './routes/stacks';

import AsyncStorage from '@react-native-async-storage/async-storage';
//import AsyncStorage from "@react-native-community/async-storage";

//const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();
/*
const MainDrawer = () => (
    <Drawer.Navigator
    >
        <Drawer.Screen name="Home" component={HomeStack}/>
        <Drawer.Screen name="Videos" component={VideosStack}/>
   
    </Drawer.Navigator>
)
*/
const LoginStack = createStackNavigator();
export const LoginStackScreen = () => (
  <LoginStack.Navigator
    screenOptions={{
      headerShown: false,
      gestureEnabled: true,
      cardOverlayEnabled: true,
    }}
    mode="modal"
  >
    <LoginStack.Screen name="LoginScreen" component={SignInScreen} />
    {/*<LoginStack.Screen name='ForgetPwScreen' component={ForgetPwScreen} />*/}
  </LoginStack.Navigator>
);

const AuthStack = createStackNavigator();
export const AuthStackScreen = () => {
  return (
    <AuthStack.Navigator
      screenOptions={{
        headerShown: false,
      }}
    >
      <AuthStack.Screen 
        name="LoginScreen" 
        component={LoginStackScreen} 
        options={{
          animationEnabled: false,
        }}
      />
      <AuthStack.Screen 
        name="SignUpScreen" 
        component={SignUpScreen} 
        options={{
          animationEnabled: false,
        }}
      />
    </AuthStack.Navigator>
  );
};

const HomeStack = createStackNavigator();
export const HomeStackScreen = () => (
  <HomeStack.Navigator
    screenOptions={{
      headerShown: false,
    }}
  >
    <HomeStack.Screen
      name="Home"
      component={HomeScreen}
      options={{
        animationEnabled: false,
      }}
    />
  </HomeStack.Navigator>
);
const MainStack = createStackNavigator();
export const MainStackScreen = () => (
  <MainStack.Navigator
    screenOptions={{
      headerShown: false,
    }}
  >
    <MainStack.Screen
      name="Main"
      component={MainScreen}
      options={{
        animationEnabled: false,
      }}
    />
    <MainStack.Screen
      name="ProductScreen"
      component={ProductScreen}
      options={{
        animationEnabled: false,
      }}
    />
  </MainStack.Navigator>
);

const CartStack = createStackNavigator();
export const CartStackScreen = () => (
  <CartStack.Navigator
    screenOptions={{
      headerShown: false,
    }}
  >
    <CartStack.Screen
      name="Cart"
      component={CartScreen}
      options={{
        animationEnabled: false,
      }}
    />
  </CartStack.Navigator>
)

const ProfileStack = createStackNavigator();
export const ProfileStackScreen = () => (
  <ProfileStack.Navigator
    screenOptions={{
      headerShown: false,
    }}
  >
    <ProfileStack.Screen
      name="Profile"
      component={ProfileScreen}
      options={{
        animationEnabled: false,
      }}
    />
  </ProfileStack.Navigator>
);

const Tab = createBottomTabNavigator();
export const TabScreen = () => {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused }) => {
          let iconName;
          const color = focused ? "green" : "grey";
          if (route.name === "HomeTab") {
            iconName = "logout";
          } else if (route.name === "MainTab") {
            iconName = "home";
          } else if (route.name === "CartTab") {
            iconName = "shoppingcart";
          } else if (route.name === "ProfileTab") {
            iconName = "profile";
          }
          return <AntDesign name={iconName} size={23} color={color} />;
        },
      })}
      barStyle={{
        backgroundColor: "grey",
        height: 50,
        justifyContent: "center",
      }}
      activeColor={"green"}
      inactiveColor={"grey"}
    >
      <Tab.Screen
        name="MainTab"
        component={MainStackScreen}
        options={{
          tabBarLabel: "Main",
          animationEnabled: false,
        }}
      />
      <Tab.Screen
        name="HomeTab"
        component={HomeStackScreen}
        options={{
          tabBarLabel: "Logout",
          animationEnabled: false,
        }}
      />
      <Tab.Screen
        name="CartTab"
        component={CartStackScreen}
        options={{
          tabBarLabel: "Cart",
          animationEnabled: false,
        }}
      />
      <Tab.Screen
        name="ProfileTab"
        component={ProfileStackScreen}
        options={{
          tabBarLabel: "Profile",
          animationEnabled: false,
        }}
      />
    </Tab.Navigator>
  );
};

const UserListStack = createStackNavigator();
export const UserListStackScreen = () => (
  <UserListStack.Navigator
    screenOptions={{
      headerShown: false,
    }}
  >
    <UserListStack.Screen
      name="UserList"
      component={UserListScreen}
      options={{
        animationEnabled: false,
      }}
    />
  </UserListStack.Navigator>
);

const AdminTab = createBottomTabNavigator();
export const AdminTabScreen = () => {
  return (
    <AdminTab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused }) => {
          let iconName;
          const color = focused ? "green" : "grey";
          if (route.name === "HomeTab") {
            iconName = "logout";
          } else if (route.name === "MainTab") {
            iconName = "home";
          } else if (route.name === "CartTab") {
            iconName = "shoppingcart";
          } else if (route.name === "ProfileTab") {
            iconName = "profile";
          }else if (route.name === "UserListTab") {
            iconName = "addusergroup";
          }
          return <AntDesign name={iconName} size={23} color={color} />;
        },
      })}
      barStyle={{
        backgroundColor: "grey",
        height: 50,
        justifyContent: "center",
      }}
      activeColor={"green"}
      inactiveColor={"grey"}
    >
      <Tab.Screen
        name="MainTab"
        component={MainStackScreen}
        options={{
          tabBarLabel: "Main",
          animationEnabled: false,
        }}
      />
      <Tab.Screen
        name="HomeTab"
        component={HomeStackScreen}
        options={{
          tabBarLabel: "Logout",
          animationEnabled: false,
        }}
      />
      <Tab.Screen
        name="CartTab"
        component={CartStackScreen}
        options={{
          tabBarLabel: "Cart",
          animationEnabled: false,
        }}
      />
      <Tab.Screen
        name="ProfileTab"
        component={ProfileStackScreen}
        options={{
          tabBarLabel: "Profile",
          animationEnabled: false,
        }}
      />
      <AdminTab.Screen
        name="UserListTab"
        component={UserListStackScreen}
        options={{
          tabBarLabel: "UserList",
          animationEnabled: false,
        }}
      />
    </AdminTab.Navigator>
  );
};


