//import 'react-native-gesture-handler';
import React, { Component, useState, useEffect } from "react";
import { View, Text } from "react-native";
import { connect } from "react-redux";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
//import { Ionicons } from "@expo/vector-icons";
import { MaterialCommunityIcons, AntDesign } from "@expo/vector-icons";

import * as SecureStore from "expo-secure-store";
//import AuthScreen from './components/auth';
import Welcome from "./screens/Welcome";
import SignInScreen from "./screens/SignInScreen";
import SignUpScreen from "./screens/RegisterScreen";
import MainScreen from "./screens/MainScreen";
import HomeScreen from "./screens/HomeScreen";
import { useDispatch, useSelector } from 'react-redux'
import ProductScreen from "./screens/ProductScreen";
import ProfileScreen from "./screens/ProfileScreen";
import { login } from "../actions/userActions";
//import ProfileScreen from './components/user/profile/profile';
//import { Stack, HomeStack, VideosStack, screenOptions } from './routes/stacks';

//import AsyncStorage from '@react-native-async-storage/async-storage';
import AsyncStorage from "@react-native-community/async-storage";

//const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();
/*
const MainDrawer = () => (
    <Drawer.Navigator
    >
        <Drawer.Screen name="Home" component={HomeStack}/>
        <Drawer.Screen name="Videos" component={VideosStack}/>
   
    </Drawer.Navigator>
)
*/
const LoginStack = createStackNavigator();
export const LoginStackScreen = () => (
  <LoginStack.Navigator
    screenOptions={{
      headerShown: false,
      gestureEnabled: true,
      cardOverlayEnabled: true,
    }}
    mode="modal"
  >
    <LoginStack.Screen name="LoginScreen" component={SignInScreen} />
    {/*<LoginStack.Screen name='ForgetPwScreen' component={ForgetPwScreen} />*/}
  </LoginStack.Navigator>
);

const AuthStack = createStackNavigator();
export const AuthStackScreen = () => {
  return (
    <AuthStack.Navigator
      screenOptions={{
        headerShown: false,
      }}
    >
      <AuthStack.Screen name="LoginScreen" component={LoginStackScreen} />
    </AuthStack.Navigator>
  );
};

const HomeStack = createStackNavigator();
export const HomeStackScreen = () => (
  <HomeStack.Navigator
    screenOptions={{
      headerShown: false,
    }}
  >
    <HomeStack.Screen
      name="Home"
      component={HomeScreen}
      //animationEnabled: false
    />
  </HomeStack.Navigator>
);

const Tab = createBottomTabNavigator();
export const TabScreen = () => {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused }) => {
          let iconName;
          const color = focused ? "green" : "grey";
          if (route.name === "HomeTab") {
            iconName = "home";
          } else if (route.name === "Favorite") {
            iconName = "hearto";
          } else if (route.name === "Cart") {
            iconName = "shoppingcart";
          }
          return <AntDesign name={iconName} size={23} color={color} />;
        },
      })}
      barStyle={{
        backgroundColor: "grey",
        height: 50,
        justifyContent: "center",
      }}
      activeColor={"green"}
      inactiveColor={"grey"}
    >
      <Tab.Screen
        name="HomeTab"
        component={HomeStackScreen}
        options={{
          tabBarLabel: "Home",
        }}
      />
    </Tab.Navigator>
  );
};

const Drawer = createDrawerNavigator();
export const DrawerNavigator = () => {
  //const user = useSelector((state) => state.auth.user);
  const userLogin = useSelector((state) => state.userLogin.userInfo)
  userLogin &&
  console.log("userLogin",userLogin["token"])
  
  const user = "";

  return (
    <Drawer.Navigator
      drawerContentOptions={{
        itemStyle: { marginVertical: 3 },
      }}
    >
        
        {
          userLogin &&
        userLogin["email"] != "" && userLogin["isAdmin"] == "false" ? (
        
        <Drawer.Screen
            key="HomeTab"
            name="HomeTab"
            component={TabScreen}
            options={() => ({
            title: ({ focused }) => (
                <Text
                style={{
                    fontSize: 14,
                    fontWeight: "500",
                    fontFamily: "Roboto-Medium",
                }}
                >
                Home
                </Text>
            ),
            drawerIcon: ({ focused }) => (
                <MaterialCommunityIcons
                name="home-outline"
                size={23}
                //color={focused ? Colors.lighter_green : Colors.grey}
                />
            ),
            })}
            
        />
        
        ):null
        }
      {user == "" ? (
        <Drawer.Screen
          name="AuthStack"
          component={AuthStackScreen}
          options={() => ({
            title: ({ focused }) => (
              <Text
                style={{
                  fontSize: 14,
                  fontWeight: "500",
                  fontFamily: "Roboto-Medium",
                }}
              >
                Login
              </Text>
            ),
            drawerIcon: ({ focused }) => (
              <MaterialCommunityIcons
                name="login"
                size={23}
                //color={focused ? Colors.lighter_green : Colors.grey}
              />
            ),
          })}
        />
      ) : (
        <Drawer.Screen
          name="Welcome"
          component={Welcome}
          options={() => ({
            title: ({ focused }) => (
              <Text
                style={{
                  fontSize: 14,
                  fontWeight: "500",
                  fontFamily: "Roboto-Medium",
                }}
              >
                Welcome
              </Text>
            ),
            drawerIcon: ({ focused }) => (
              <MaterialCommunityIcons
                name="welcome"
                size={23}
                //color={focused ? Colors.lighter_green : Colors.grey}
              />
            ),
          })}
        />
      )}
      
    </Drawer.Navigator>
  );
};
