import React, { useEffect, useState, useContext } from "react";
import { useNavigation } from "@react-navigation/native";
import {
  Text,
  TextInput,
  View,
  TouchableOpacity,
  Alert,
  Button,
  StyleSheet,
  SafeAreaView
} from "react-native";
//import { MaterialCommunityIcons } from "@expo/vector-icons";
//import axios from "axios";
import { useDispatch, useSelector } from 'react-redux'
import { KeyboardAwareScrollView } from "react-native-keyboard-aware-scroll-view";
//import { firebase,firebaseLoggedInDetail,firebaseUsers } from "../components/Firebase/firebase";
import { login } from "../actions/userActions"
import AuthGlobal from "../../Context/store/AuthGlobal";
//import { navigationRef } from "../RootNavigation";
import * as RootNavigation from "../RootNavigation";
 
export default function SignInScreen({ navigation }) {
  const context = useContext(AuthGlobal)
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  //const navigation = useNavigation();
  const dispatch = useDispatch()
  const userLogin = useSelector((state) => state.userLogin)
  const { loading, error, userInfo } = userLogin
  const [error1,setError1] = useState("");
  //console.log(userLogin)

  useEffect(() => {
    console.log("context",context)
    //console.log(userInfo)
    /*
    if(userInfo) {
      navigation.navigate("Home")
    }
     */
  },[userInfo])

  const handleSubmit = async (e) => {
    //const loginToken;
    //const loginId;
    let today = new Date();
    let dateTime = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate() + " " + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    let userMail = "";
    //e.preventDefault()
    try {
    await dispatch(login(email,password))
    navigation.navigate("HomeTab",{ screen: 'HomeTab'})
    console.log(error)
    } catch (err) {
      console.log("err")
      //navigation.navigate("HomeTab",{ screen: 'HomeTab'})
      setError1(err)
    }
    if(userInfo) {
      
      //setuserInfo(userInfo)
      console.log("running")
      //navigation.navigate("SignUp")
      //console.log(userInfo)
    }
  };
 
  
  return (
    <KeyboardAwareScrollView
      extraScrollHeight={110}
      contentContainerStyle={styles.container}
    >
      <SafeAreaView style={{ alignItems: "center" }}>
        <Text style={styles.loginText}>Login To Download</Text>
        <View style={styles.form}>
          <TextInput
            autoCapitalize="none"
            style={styles.textInput}
            placeholder="email"
            placeholderTextColor="#E1E1E1"
            onChangeText={text => setEmail(text)}
            value={email}
          />
          <TextInput
            style={styles.textInput}
            placeholder="password"
            placeholderTextColor="#E1E1E1"
            secureTextEntry={true}
            autoCapitalize="none"
            onChangeText={text => setPassword(text)}
            value={password}
          />
          {
            error ?
              <Text style={{color:"red",fontSize:"10"}}>{error}</Text>:null
          }
          <TouchableOpacity style={styles.button} onPress={handleSubmit}>
            <Text style={styles.buttonText}>Login</Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => {
              navigation.navigate("SignUp");
            }}
          >
            <Text style={styles.underButton}>Didnt have account! Register</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    </KeyboardAwareScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "black",
    alignItems: "center",
    justifyContent: "center"
  },
  button: {
    width: 190,
    height: 65,
    borderRadius: 50,
    backgroundColor: "tomato",
    justifyContent: "center",
    alignItems: "center",
    marginTop: 50
  },
  loginText:{
    color:"white",
    fontSize:40,
    fontWeight:"bold"
  },
  buttonText: {
    color: "white",
    fontSize: 24
  },
  underButton: {
    marginTop: 15,
    color: "white",
    textDecorationLine: "underline"
  },
  textInput: {
    borderBottomColor: "white",
    borderBottomWidth: 1,
    width: 330,
    height: 45,
    marginBottom: 30,
    color: "white"
  },
  form: {
    justifyContent: "center",
    alignItems: "center",
    marginTop: 50
  }
});
